package com.hva.helios;

import com.hva.helios.data.*;
import com.hva.helios.models.*;
import com.hva.helios.models.user.Admin;
import com.hva.helios.models.user.Client;
import com.hva.helios.models.user.Specialist;
import com.hva.helios.models.user.hour.AvailableHour;
import com.hva.helios.models.user.skill.Skill;
import com.hva.helios.models.user.skill.UserSkill;
import com.hva.helios.repositories.AnnouncementRepository;
import com.hva.helios.repositories.FileJPARepository;
import com.hva.helios.repositories.ProjectJPARepository;
import com.hva.helios.repositories.interfaces.EntityRepository;
import com.hva.helios.repositories.interfaces.jpa.*;
import com.hva.helios.repositories.interfaces.testRepo;
import com.hva.helios.repositories.user.UserSkillJPARepository;
import org.apache.tomcat.util.http.fileupload.IOUtils;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.boot.CommandLineRunner;
import org.springframework.boot.SpringApplication;
import org.springframework.boot.autoconfigure.SpringBootApplication;
import org.springframework.context.ConfigurableApplicationContext;

import javax.transaction.Transactional;
import java.io.*;
import java.util.*;

@SpringBootApplication
public class HeliosApplication implements CommandLineRunner {
	Random random = new Random();

	public static void main(String[] args) {
		ConfigurableApplicationContext applicationContext = SpringApplication.run(HeliosApplication.class, args);
	}

	@Autowired
	private EntityRepository<Skill> skillRepo;

	@Autowired
	private UserJPARepository userRepository;

	@Autowired
	private SpecialistJPARepository specialistRepo;

	@Autowired
	private ClientJPARepository clientRepository;

	@Autowired
	private AdminJPARepository adminRepository;
	@Autowired
	private EventJPARepository eventRepo;

	@Autowired
	private testRepo projectRepo;


	@Autowired
	private UserSkillJPARepository userSkillRepo;

	@Autowired
	private ProjectJPARepository projectRepository;

	@Autowired
	private EntityRepository<AvailableHour> availableHourRepo;

	@Autowired
	private FileJPARepository fileRepo;

	@Autowired
	private AnnouncementRepository announcementRepository;

	@Override
	@Transactional
	public void run(String... args) {
		System.out.println("Running CommandLine Startup");

		createInitialSkillData();
		createInitialAdmins();
		createInitialSpecialists();
		createInitialClients();
		createInitialProjects();
		createInitialHoursData();
		createInitialEvents();
		createInitialUserSkill();
        createInitialAnnouncements();
		try {
			createInitialFiles();
		} catch (IOException e) {
			throw new RuntimeException(e);
		}
	}

	private void createInitialAdmins() {
		UserData adminData = new UserData();

		for (User user: adminData.getAdmins()){
			Admin nAdmin = adminRepository.save(new Admin());

			user.setAdmin(nAdmin);

			userRepository.save(user);
		}
	}

	private void createInitialSpecialists() {
		UserData specialistData = new UserData();

		for (User user: specialistData.getSpecialists()){
			Specialist nSpecialist = specialistRepo.save(user.getSpecialist());

			user.setSpecialist(nSpecialist);

			userRepository.save(user);
		}
	}

	private void createInitialClients() {
		UserData clientData = new UserData();

		for (User user: clientData.getClients()){
			Client nClient = clientRepository.save(user.getClient());

			user.setClient(nClient);

			userRepository.save(user);
		}
	}

	private void createInitialProjects() {
		ProjectData projectData = new ProjectData();

		// this piece of code serves the purpose of testing if projects actually show up specifically for the specialist
		Project nProject = new Project("test project voor specialist", userRepository.findByEmail("client"), null, 0,new Date(),"test");
		nProject.setSpecialists(new HashSet<>(specialistRepo.findById(1)));
		projectRepo.save(nProject);

		Project n2Project = new Project("test project voor specialist2", userRepository.findByEmail("dennis.moesClient@hva.nl"), null, 0,new Date(),"test");
		n2Project.setSpecialists(new HashSet<>(specialistRepo.findById(2)));
		projectRepo.save(n2Project);


		Project n1Project = new Project("test project voor specialist3", userRepository.findByEmail("client"), null, 0,new Date(),"test");
		n1Project.setSpecialists(new HashSet<>(specialistRepo.findAll()));
		projectRepo.save(n1Project);


		for (Project project: projectData.getProjects()){
			System.out.println("SOUT "+project.getBannerUrl());
			project.setUser(userRepository.findByEmail("client"));
			project.setSpecialists(new HashSet<>(specialistRepo.findById(1)));
			projectRepo.save(project);
		}
	}

	private void createInitialHoursData() {
		List<AvailableHour> hours = availableHourRepo.findAll();
		List<Specialist> specialists = specialistRepo.findAll();
		if (hours.size() > 0) return;
		System.out.println("Configuring some initial hours in the repository");

		AvailableHour hour = new AvailableHour();

		for (Specialist specialist : specialists) {
			specialist.setHours(hour);
		}
	}

	private void createInitialEvents() {
		List<Event> events = eventRepo.findAll();
		if (events.size() > 0) return;
		System.out.println("Configuring some initial events in the repository");

		Specialist specialist = userRepository.findByEmail("specialist").getSpecialist();
		Project project = projectRepo.findAll().get(0);
		EventData eventData = new EventData(specialist);

		for (Event event : eventData.getEvents()) {
			event.setAccepted(random.nextInt(3) - 1);

			eventRepo.save(event);

			event.associateSpecialist(specialist);
			event.associateProject(project);
		}
	}

	private void createInitialSkillData() {
		List<Skill> skills = skillRepo.findAll();
		if (skills.size() > 0) return;
		System.out.println("Configuring some initial skills in the repository");

		SkillData skillData = new SkillData();

		for (Skill skill : skillData.getAvailableSkills()) {
			skillRepo.save(skill);
		}
	}

	private void createInitialUserSkill() {
		Skill skill = skillRepo.findAll().get(0);
		Specialist specialist = userRepository.findByEmail("specialist").getSpecialist();
		List<UserSkill> userSkills = userSkillRepo.findAll();
		if (userSkills.size() > 0) return;
		System.out.println("Configuring some initial user skills in the repository");

		UserSkill userSkill = new UserSkill(skill, 4, specialist);
		specialist.associateUserSkill(userSkill);

		userSkillRepo.save(userSkill);
	}

	private void createInitialFiles() throws IOException {
		boolean isInTest = false;

		for (StackTraceElement element : Thread.currentThread().getStackTrace()) {
			if (element.getClassName().startsWith("org.junit.")) {
				isInTest = true;
			}
		}

		String fileData = "data:application/json;base64,";
		FileModel cvFile;

		User specialist = userRepository.findByEmail("specialist");
		List<FileModel> files = fileRepo.findAll();

		if (files.size() > 0) return;
		System.out.println("Configuring some initial files in the repository");

//		String path = isInTest ? "src/main/resources/cvData.txt" : "backend/src/main/resources/cvData.txt";


		try {
//			FileInputStream file = new FileInputStream("../../../resources/cvData.txt");

			InputStream in = getClass().getResourceAsStream("/cvData.txt");
			BufferedReader reader = new BufferedReader(new InputStreamReader(in));

			File file = new File(String.valueOf(reader));

			FileInputStream fileStream = new FileInputStream(file);

			cvFile = new FileModel(specialist.getId(), "cv", "application/pdf", fileStream.readAllBytes());
			fileRepo.save(cvFile);
		} catch (IOException e) {
			System.out.println(e.getMessage());
			throw new IOException(e.getMessage());
		}

		FileModel pfImageFile = new FileModel(specialist.getId(), "profileImage", "image/jpeg", fileData.getBytes());
		fileRepo.save(pfImageFile);

		specialist.setPhoto(pfImageFile.getId());
		userRepository.save(specialist);
	}

	/**
	 * Saves a list of announcements to the database.
	 *
	 */
	private void createInitialAnnouncements() {
		AnnouncementData announcementData = new AnnouncementData();
		User user = userRepository.findByEmail("client");
		Project project = projectRepository.findById(1);

		// Set the user and project for each announcement
		announcementData.getAnnouncements().forEach(a -> {
			a.setUser(user);
			a.setProject(project);
			// Save the announcement to the database
			announcementRepository.save(a);
		});
	}
}
